import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/notificationlist_item_model.dart';import 'package:dondondony38_s_application1/presentation/brand_infotwo_screen/models/brand_infotwo_model.dart';part 'brand_infotwo_event.dart';part 'brand_infotwo_state.dart';/// A bloc that manages the state of a BrandInfotwo according to the event that is dispatched to it.
class BrandInfotwoBloc extends Bloc<BrandInfotwoEvent, BrandInfotwoState> {BrandInfotwoBloc(BrandInfotwoState initialState) : super(initialState) { on<BrandInfotwoInitialEvent>(_onInitialize); }

_onInitialize(BrandInfotwoInitialEvent event, Emitter<BrandInfotwoState> emit, ) async  { emit(state.copyWith(brandInfotwoModelObj: state.brandInfotwoModelObj?.copyWith(notificationlistItemList: fillNotificationlistItemList()))); } 
List<NotificationlistItemModel> fillNotificationlistItemList() { return [NotificationlistItemModel(imageClass: ImageConstant.imgTelevision, text1: "1000 riyals have been deposited in your wallet", text2: "30 may 2023 - 6:55 PM", text3: "+1000\nEGP"), NotificationlistItemModel(imageClass: ImageConstant.imgTelevisionRedA200, text1: "1000 riyals were paid in an invoice", text2: "30 may 2023 - 6:55 PM", text3: "-1000 EGP"), NotificationlistItemModel(imageClass: ImageConstant.imgTelevision, text1: "1000 riyals have been deposited in your wallet", text2: "30 may 2023 - 6:55 PM", text3: "+1000\nEGP")]; } 
 }
